package type;

import java.io.IOException;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Disabled;
import org.junit.jupiter.api.Test;
import org.nervos.ckb.crypto.Hash;
import org.nervos.ckb.service.Api;
import org.nervos.ckb.type.Script;

/** Copyright Â© 2018 Nervos Foundation. All rights reserved. */
@Disabled
public class ScriptTest {

  @Test
  public void testScriptHashWithCodeHash() throws IOException {
    Api api = new Api("http://localhost:8114");
    String codeHash =
        Hash.blake2b(
            "");
    Script script = new Script(codeHash, "0x", Script.DATA);
    Assertions.assertEquals(api.computeScriptHash(script), script.computeHash());
  }
}
