package utils;

import com.google.gson.Gson;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;
import org.nervos.ckb.service.GsonFactory;
import org.nervos.ckb.type.Header;
import org.nervos.ckb.type.transaction.Transaction;
import org.nervos.ckb.utils.Numeric;
import org.nervos.ckb.utils.Serializer2;

import java.io.IOException;
import java.io.Reader;
import java.nio.file.Files;
import java.nio.file.Paths;

public class Serializer2Test {
  @Test
  public void testTransaction() throws IOException {
    Transaction transaction = readData("transaction.json", Transaction.class);

    assertByteArray("0xf8de3bb47d055cdf460d93a2a6e1b05f7432f9777c8c474abf4eec1d4aee5d370000000001",
        Serializer2.serialize(transaction.cellDeps.get(0)));
    assertByteArray("0x8f8c79eb6671709633fe6a46de93c0fedc9c1b8a6527a18d3983879542635c9f0200000000",
        Serializer2.serialize(transaction.cellDeps.get(1)));

    assertByteArray("0x000000000000000079ba9678bfc25bf8c812cc96edd2c6937cebd518c2be0dd6a1c76978a2c18fee02000000",
        Serializer2.serialize(transaction.inputs.get(0)));

    assertByteArray("",
        Serializer2.serialize(transaction.outputs.get(0)));
    assertByteArray("0x6100000010000000180000006100000002e2fa8dd7000000490000001000000030000000310000009bd7e06f3ecf4be0f2fcd2188b23f1b9fcc88e5d4b65a8637b17723bbda3cce80114000000b1354a801d92dd987486488b728154bcfbd809e1",
        Serializer2.serialize(transaction.outputs.get(1)));

    assertByteArray("",
        Serializer2.serialize(transaction, false));

    assertByteArray("",
        Serializer2.serialize(transaction, true));
  }

  @Test
  public void testHeader() throws IOException {
    Header header = readData("header.json", Header.class);
    assertByteArray("",
        Serializer2.serialize(header, true));
  }

  private void assertByteArray(String expected, byte[] actual) {
    String message = String.format("Expected: %s\nActual: %s\n", expected, Numeric.toHexString(actual));
    Assertions.assertArrayEquals(Numeric.hexStringToByteArray(expected), actual, message);
  }

  private static <T> T readData(String fileName, Class<T> clazz) {
    String filePath = "src/test/resources/serialization/" + fileName;
    try (Reader reader = Files.newBufferedReader(Paths.get(filePath))) {
      Gson gson = GsonFactory.create();
      return gson.fromJson(reader, clazz);
    } catch (IOException ex) {
      ex.printStackTrace();
      Assertions.fail(ex.getMessage());
    }
    return null;
  }

}
